    window.addEventListener("scroll", () => {
      if (window.scrollY > 10) {
        setExpand(false);
      } else {
        setExpand(true);
      }
    });
    return () => {
      window.removeEventListener("scroll", () => {
        if (window.scrollY > 10) {
          setExpand(false);
        } else {
          setExpand(true);
        }
      });
    };
